import React, { Component } from 'react'
import Web3 from 'web3'
import Token from '../abis/Token.json'
import EthSwap from '../abis/EthSwap.json'
import Navbar from './Navbar'
import Main from './Main'
import './App.css'
import WalletConnectProvider from "@walletconnect/web3-provider"
import QRCode from 'qrcode.react';  // Librería para generar el código QR

class App extends Component {



  async componentWillMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
  }

  async loadBlockchainData() {


    // Asegúrate de que MetaMask esté disponible en el navegador (ya sea en desktop )
    if (typeof window.ethereum !== 'undefined') {
      const web3 = new Web3(window.ethereum);

    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })

    const ethBalance = await web3.eth.getBalance(this.state.account)
    this.setState({ ethBalance })

    // Load Token
    const networkId =  await web3.eth.net.getId()
    const tokenData = Token.networks[networkId]
    if(tokenData) {
      const token = new web3.eth.Contract(Token.abi, tokenData.address)
      this.setState({ token })
      this.setState({accounts: accounts[0]})
  //    let tokenBalance = await token.methods.balanceOf(accounts[0]).call()
      let tokenBalance = await token.methods.balanceOf(this.state.account).call()

    if (tokenBalance != null) {
      this.setState({ tokenBalance: tokenBalance.toString() })
    }
    } else {
      window.alert('Token contract not deployed to detected network.')
    }

    // Load EthSwap
    const ethSwapData = EthSwap.networks[networkId]
    if(ethSwapData) {
      const ethSwap = new web3.eth.Contract(EthSwap.abi, ethSwapData.address)
      this.setState({ ethSwap })
    } else {
      window.alert('EthSwap contract not deployed to detected network.')
    }

    this.setState({ loading: false })
  }else {
    //getBalance();

          // Crea un nuevo proveedor WalletConnect
         // const web3 = new Web3('https://mainnet.infura.io/v3/76624e32adc147a8894211e83952c9ec');


  }
}
  async loadWeb3() {

    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
           // Ensure the URI is correct
          const uri = "https://mainnet.infura.io/v3/76624e32adc147a8894211e83952c9ec";  // Change this to your URI
      // Create the Web3 instance
         // const window.web3 = new Web3(new Web3.providers.HttpProvider(uri));
          window.web3 = new Web3(window.ethereum)
          const accounts = '0x353c18E9C26fa6aB99C3FbB93B20A1C45B747C9B'
          //window.alert('Conectada!')
          //const accounts = window.web3.eth.getAccounts()
          this.setState({ account: '0x353c18E9C26fa6aB99C3FbB93B20A1C45B747C9B'})
          //accounts[0]='0x353c18E9C26fa6aB99C3FbB93B20A1C45B747C9B'
//const accounts = await window.web3.eth.getAccounts()

             // this.setState({ account: accounts[0]})
              window.alert(accounts[0])
             // window.alert(accounts[0] )
            //  const ethBalance = window.web3.eth.getBalance(this.state.account)
          //    this.setState({ ethBalance })





          this.setState({ account: accounts[0] })

          const ethBalance = await window.web3.eth.getBalance(this.state.account)
          this.setState({ ethBalance })

          // Load Token
          const networkId =  await window.web3.eth.net.getId()
          const tokenData = Token.networks[networkId]
          if(tokenData) {
            const token = new window.web3.eth.Contract(Token.abi, tokenData.address)
            this.setState({ token })
            this.setState({accounts: accounts[0]})
        //    let tokenBalance = await token.methods.balanceOf(accounts[0]).call()
            let tokenBalance = await token.methods.balanceOf(this.state.account).call()

          if (tokenBalance != null) {
            this.setState({ tokenBalance: tokenBalance.toString() })
          }
          } else {
            window.alert('Token contract not deployed to detected network.')
          }

          // Load EthSwap
          const ethSwapData = EthSwap.networks[networkId]
          if(ethSwapData) {
            const ethSwap = new window.web3.eth.Contract(EthSwap.abi, ethSwapData.address)
            this.setState({ ethSwap })
          } else {
            window.alert('EthSwap contract not deployed to detected network.')
          }

          this.setState({ loading: false })
            this.setState({ loading: false })            
    }

  }

  buyTokens = (etherAmount) => {
    this.setState({ loading: true })
    this.state.ethSwap.methods.buyTokens().send({ value: etherAmount, from: this.state.account }).on('transactionHash', (hash) => {
      this.setState({ loading: false })
    })
  }

  sellTokens = (tokenAmount) => {
    this.setState({ loading: true })
    this.state.token.methods.approve(this.state.ethSwap.address,tokenAmount).send({from: this.state.account }).on('transactionHash', (hash) => {
      this.state.ethSwap.methods.sellTokens(tokenAmount).send({ value: tokenAmount, from: this.state.account }).on('transactionHash', (hash) => {
        this.setState({ loading: false })
      })
    })
  }

  constructor(props) {
    super(props)
    this.state = {
      account: '',
      token: {},
      ethSwap: {},
      ethBalance: '0',
      tokenBalance: '0',
      loading: true
    }
  }

  render() {
    let content
    if(this.state.loading) {
      content = <p id="loader" className="text-center">Loading...</p>
    } else {
      content = <Main
        ethBalance={this.state.ethBalance}
        tokenBalance={this.state.tokenBalance}
        buyTokens={this.buyTokens}
        sellTokens={this.sellTokens}
      />
    }

    return (
      <div>
        <Navbar account={this.state.account} />
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 ml-auto mr-auto" style={{ maxWidth: '600px' }}>
              <div className="content mr-auto ml-auto">
                <a
                  href="http://www.dappuniversity.com/bootcamp"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                </a>

                {content}

              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
