       // console.error('Error al obtener el networkId:', error);
        window.alert(`ÀQUI01 NO Conectado a la red con ID:}`+error);
    });


// FIB

///fin

    }  

  }

  buyTokens = (etherAmount) => {

    //window.alert('this.state.account:!'+this.state.methods);
    this.setState({ loading: true })
    this.state.ethSwap.methods.buyTokens().send({ value: etherAmount, from: this.state.account }).on('transactionHash', (hash) => {
      this.setState({ loading: false })
    })
  }

  sellTokens = (tokenAmount) => {

    // window.alert('this.state.ethSwap.address!'+this.state.ethSwap.address)
    if (this.state.token && this.state.token.methods) {

      this.state.token.methods.approve(this.state.ethSwap.address, tokenAmount).send({ from: this.state.account}).then((receipt) => {
         // window.alert('yes'+receipt);

             //  window.alert('tokenAmount:!'+this.state.account);
          this.setState({ loading: true })
        //  this.state.token.methods.approve('0x353c18E9C26fa6aB99C3FbB93B20A1C45B747C9B',tokenAmount).send({from:'0x71Be5028d3288Da3712Be86B6864855F046Ae769' }).on('transactionHash', (hash) => {
         //   window.alert(' 01approve');

            this.state.ethSwap.methods.sellTokens(tokenAmount).send({ value: tokenAmount, from: '0x39B824f0dF8aECAf655277333440ECF59D0dcD19' }).on('transactionHash', (hash) => {
           //
              window.alert(' envia');
              this.setState({ loading: false })
         //   })


    })
        })
        .catch((error) => {
          window.alert('no'+error);
        });
    } else {
      window.alert('NO');
    }


  }

  constructor(props) {
    super(props)
    this.state = {
      account: '',
      token: {},
      ethSwap: {},
      ethBalance: '0',
      tokenBalance: '0',
      loading: true
    }
  }

  render() {
    let content
    if(this.state.loading) {
      content = <p id="loader" className="text-center">Loading...</p>
    } else {
      content = <Main
        ethBalance={this.state.ethBalance}
        tokenBalance={this.state.tokenBalance}
        buyTokens={this.buyTokens}
        sellTokens={this.sellTokens}
      />
    }

    return (
      <div>
        <Navbar account={this.state.account} />
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 ml-auto mr-auto" style={{ maxWidth: '600px' }}>
              <div className="content mr-auto ml-auto">
                <a
                  href="http://www.dappuniversity.com/bootcamp"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                </a>

                {content}

              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
